sys_script_include.script:
var SimulateDevOps = Class.create();
SimulateDevOps.prototype = Object.extendsObject(global.AbstractAjaxProcessor, {


    getOrchestrationEventPayload: function() {

        var simulationData = [];
        var startEvent = this.getOrchestrationStartEventPayload();
        simulationData.push(startEvent);
		var endEvent = this.getOrchestrationEndEventPayload(startEvent);
        simulationData.push(endEvent);

        return JSON.stringify(simulationData);

    },

    getOrchestrationStartEventPayload: function() {
        var pipelineSysId = this.getParameter("sysparm_pipeline_sys_id");
        var pipelineInfo = new global.GlideQuery("sn_devops_pipeline").get(pipelineSysId, ['name', 'tool.sys_id', 'pipeline_url', 'orch_pipeline']);
        gs.info("SimulateDevOps pipelineInfo " + JSON.stringify(pipelineInfo));
        if (pipelineInfo) {
            return this.getSimulationData(pipelineInfo._value);
        }
        return {};
    },

    getOrchestrationEndEventPayload: function(startEvent) {

        var simulationData = JSON.parse(JSON.stringify(startEvent));;
        simulationData.taskExecution.result = 'successful';
        simulationData.taskExecution.startDateTime = '';
        simulationData.taskExecution.endDateTime = new GlideDateTime().getDisplayValue();
        return simulationData;
    },

    getSimulationData: function(pipelineInfo) {
        var stage = "stage1";
        var buildNumber = gs.generateGUID();
		var dateTime = new GlideDateTime().getDisplayValue();

        var payloadTemplate = {
            "taskExecution": {
                "toolId": pipelineInfo.tool.sys_id,
                "buildNumber": buildNumber,
                "nativeId": buildNumber,
                "name": stage + "#" + buildNumber,
                "id": stage + "#" + buildNumber,
                "url": pipelineInfo.pipeline_url + "/" + stage + "/#" + buildNumber,
                "isMultiBranch": "false",
                "branchName": "main",
                "pipelineExecutionUrl": pipelineInfo.pipeline_url + "/" + stage + "/" + buildNumber,
                "orchestrationTaskUrl": pipelineInfo.pipeline_url + "/" + stage + "/",
                "orchestrationTaskName": pipelineInfo.orch_pipeline + "#" + stage,
                "result": "building",
                "startDateTime": dateTime,
                "upstreamId": ""
            },
            "orchestrationTask": {
                "orchestrationTaskURL": pipelineInfo.pipeline_url + "/" + stage + "/",
                "orchestrationTaskName": pipelineInfo.orch_pipeline + "#" + stage,
                "branchName": "main",
                "toolId": pipelineInfo.tool.sys_id
            }
        };

        return payloadTemplate;
    },

    type: 'SimulateDevOps'
});
